import { useState } from "react";

function CreateArea(props) {
    const [inputNote, setInputNote] = useState({
        inputTitle: "",
        inputContent: ""
    });

    function handleChange(event){
        const {name, value}=event.target;

        setInputNote(prevValue => {
            if (name=="title"){
                return{
                    inputTitle: value,
                    inputContent: prevValue.inputContent
                }
            }else if(name=="content"){
                return{
                    inputTitle: prevValue.inputTitle,
                    inputContent: value
                }
            }
        })
    }

    return (
        <div>
            <form >

            <input onChange={handleChange} name="title" placeholder="Title" value={inputNote.inputTitle} />

            <textarea onChange={handleChange} name="content" placeholder="Take a note..." rows="3" value={inputNote.inputContent} />

            <button onClick={() => {
                props.onAdd(inputNote);
                // setInputNote=({
                //     inputTitle: "",
                //     inputContent: ""
                // });
            }}>Add</button>

            </form>
        </div>
    );
}

export default CreateArea;
